import React, { useState } from 'react';
import "./Home.css";
import fitjim from "../../resource/logo.jpg"

export function Home() {
  const [showThankYou, setShowThankYou] = useState(false);

  return (
    <>
    <div>
        <h2>Üdvözöl a Fitnesz Felszerelések Webáruháza!</h2>
        <p>Itt megtalálod a legjobb edzőtermi felszereléseket!</p>
        <div className="container">
            <img src={fitjim} alt="Logo" className="centered-image" />
        </div>
    </div>

    {showThankYou && (
        <div style={{ position: 'fixed', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', padding: '20px', background: 'grey', zIndex: '999', boxShadow: '0 0 10px rgba(0, 0, 0, 0.3)' }}>
          <h3>Köszönjük vásárlását</h3>
          <button onClick={() => setShowThankYou(false)}>Bezárás</button>
        </div>
      )}
    </>
  );
};
