import React, { useState, useEffect } from 'react';
import supplementsData from '../../datas/supplements.json';

export function Supplements() {
  const [supplements, setSupplements] = useState([]);

  useEffect(() => {
    setSupplements(supplementsData.supplements);
  }, []);

  const getImagePath = (imageName) => {
    return require(`../../resource/supplements/${imageName}`);
  };

    return (
    <>
    <h2>Táplálékkiegészítők</h2>
    <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}></div>
    {supplements.map((item,index) => (
      <div key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
        <img key={index} src={getImagePath(item.img)} alt={item.name} style={{ width: '350', marginRight: '20px' }} />
        <div>
          <h3>{item.name}</h3>
          <p>Ár: {item.price} Ft</p>
        </div>
      </div>
    ))}

    </>
      );
};
