import React from 'react';
import { useData } from '../../ContextAPI';
import { useNavigate } from 'react-router-dom';

export function Admin() {
    const navigate = useNavigate();
    const { supplements, machines, additionals } = useData();

    const handleDelete = (type, index) => {
        // Function to delete item based on the type (Supplements, Machines, Additionals) and index
        // For example, if type is "Supplements", use supplements.splice(index, 1) to delete
        // Don't mutate the state directly, create a copy and set the state using your context API
    };

    return (
        <>
        <div>
            <h2>Admin oldal</h2>
            <div>
                <h3 style={{ fontSize:"40px" }}>Táplálék</h3>
                <ul>
                    {supplements.map((item, index) => (
                        <li key={index} style={{ fontSize:"25px" }}>
                            {item.name}
                            <button onClick={() => handleDelete('Supplements', index)}>Törlés</button>
                            <button onClick={() => navigate(`/Edit/${item.id}`)}>Szerkesztés</button>
                        </li>
                    ))}
                </ul>
            </div>
            <div>
                <h3 style={{ fontSize:"40px" }}>Machines</h3>
                <ul>
                    {machines.map((item, index) => (
                        <li key={index}>
                            {item.name}
                            <button onClick={() => handleDelete('Machines', index)}>Törlés</button>
                            <button onClick={() => navigate(`/Edit/${item.id}`)}>Szerkesztés</button>
                        </li>
                    ))}
                </ul>
            </div>
            <div>
                <h3 style={{ fontSize:"40px" }}>Additionals</h3>
                <ul>
                    {additionals.map((item, index) => (
                        <li key={index}>
                            {item.name}
                            <button onClick={() => handleDelete('Additionals', index)}>Törlés</button>
                            <button onClick={() => navigate(`/Edit/${item.id}`)}>Szerkesztés</button>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
        </>
    );
};
