import React, { useContext } from 'react';
import { DataContext } from '../../ContextAPI';

export function SearchedItems({ searchString }) {
    const { supplements, machines, additionals } = useContext(DataContext);

    const filteredProducts = [
        ...supplements.filter((product) =>
            product.name.toLowerCase().includes(searchString)
        ),
        ...machines.filter((product) =>
            product.name.toLowerCase().includes(searchString)
        ),
        ...additionals.filter((product) =>
            product.name.toLowerCase().includes(searchString)
        ),
    ];

    return (
        <div>
            <h2>Keresés eredménye</h2>
            <ul>
                {filteredProducts.map(product => (
                    <li key={product.id}>
                        <h3>{product.name}</h3>
                        <p>Ár: {product.price}</p>
                        <p>{product.description}</p>
                        {/* További termék részletek */}
                    </li>
                ))}
            </ul>
        </div>
    );
};
