import React, { useState,useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { DataContext } from '../../ContextAPI';

export function Pay({ setShowThankYou }) {
    const navigate = useNavigate();
    const { cartItems, setCartItems, supplements, machines, additionals, setSupplements, setMachines } = useContext(DataContext);
    const [cardNumber, setCardNumber] = useState('');
    const [expirationDate, setExpirationDate] = useState('');
    const [cvv, setCVV] = useState('');
    const [address, setAddress] = useState('');

    const handleSuccessfulPurchase = () => {
      //Összes elem eltávolítása a kosárból
      setCartItems({});

      //Termékek mennyiségének csökkentése
      Object.keys(cartItems).forEach((itemName) => {
        const item = findItem(itemName);
        if (item) {
          const updatedItem = { ...item };
          updatedItem.quantity -= cartItems[itemName];
          updateItem(updatedItem);
        }
      });

      setShowThankYou(true);
      navigate('/');
    };

    const findItem = (itemName) => {
      const allItems = [...supplements, ...machines, ...additionals];
      return allItems.find((item) => item.name === itemName);
    };

    const updateItem = (updatedItem) => {
      // Frissítsd az adott elemet az összes adatlistában (supplements, machines, additionals)
      // Pl. a supplements frissítése:
      const updatedSupplements = supplements.map((item) => (item.name === updatedItem.name ? updatedItem : item));
      // Ugyanezt meg kell tenni a machines és additionals esetében is
  
      // Állítsd be az új adatlistákat a ContextAPI-ben
      setSupplements(updatedSupplements);
      // setMachines, setAdditionals hasonlóan
    };

    const handleSubmit = (e) => {
      e.preventDefault();

      //Sikeres fizetés
      if (cardNumber && expirationDate && cvv && address) {
        handleSuccessfulPurchase();
      } else {
        //Nincs minden mező kitöltve
        alert('Kérlek, tölts ki minden mezőt!');
      }
      
    };

    return (
      <>
        <h2>Fizetés</h2>
        <form onSubmit={handleSubmit}>
        <label>
          Bankkártya száma:
          <input
            type="number"
            value={cardNumber}
            onChange={(e) => setCardNumber(e.target.value)}
            style={{
              backgroundColor: 'lightgrey',
              border: '2px solid red',
              marginBottom: '10px',
              padding: '5px',
              borderRadius: '5px',
            }}
          />
        </label>
        <br></br>
        <label>
          Lejárati dátum:
          <input
            type="number"
            value={expirationDate}
            onChange={(e) => setExpirationDate(e.target.value)}
            style={{
              backgroundColor: 'lightgrey',
              border: '2px solid red',
              marginBottom: '10px',
              padding: '5px',
              borderRadius: '5px',
            }}
          />
        </label>
        <br></br>
        <label>
          CVV:
          <input
            type="number"
            value={cvv}
            onChange={(e) => setCVV(e.target.value)}
            style={{
              backgroundColor: 'lightgrey',
              border: '2px solid red',
              marginBottom: '10px',
              padding: '5px',
              borderRadius: '5px',
            }}
          />
        </label>
        <br></br>
        <label>
          Lakcím:
          <input
            type="text"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            style={{
              backgroundColor: 'lightgrey',
              border: '2px solid red',
              marginBottom: '10px',
              padding: '5px',
              borderRadius: '5px',
            }}
          ></input>
        </label>
        <br></br>
        <br></br>
        <button type="submit" style={{ color: 'white',backgroundColor: '#cc3333',height: '50px',width: '100px',fontSize: '20px',border: '3px solid white',borderRadius: '10px',marginLeft: '20px' }}>Fizetés</button>
      </form>
      </>

      );
};
