import React, { useEffect } from 'react';
import { useData } from '../../ContextAPI';
import { useNavigate, useParams } from 'react-router-dom';

export function Edit() {
    const navigate = useNavigate();
    const { supplements, machines, additionals } = useData();
    const { itemName } = useParams();
    const [itemData, setItemData] = useState({
        name: '',
        price: '',
        quantity: '',
        description: ''
    });

    useEffect(() => {
        const selectedItem = [...filteredSupplements, ...filteredMachines, ...filteredAdditionals].find(
            (item) => item.name === itemName
        );

        if(selectedItem){
            setItemData({
                name: selectedItem.name,
                price: selectedItem.price.toString(),
                quantity: selectedItem.quantity.toString(),
                description: selectedItem.description
            });
        }
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
    }

    return (
        <>
        <div>
            <h2>{itemName} szerkesztése</h2>
        </div>
        </>
    );
};
