import React, { useEffect } from 'react';
import { useData } from '../../ContextAPI';
import { useNavigate, useParams } from 'react-router-dom';

export function Edit() {
    const navigate = useNavigate();
    const { supplements, machines, additionals } = useData();
    const { itemName } = useParams();
    const [itemData, setItemData] = useState({
        name: '',
        price: '',
        quantity: '',
        description: ''
    });

    useEffect(() => {
        const selectedItem = [...filteredSupplements, ...filteredMachines, ...filteredAdditionals].find(
            (item) => item.name === itemName
        );

        if(selectedItem){
            setItemData({
                name: selectedItem.name,
                price: selectedItem.price.toString(),
                quantity: selectedItem.quantity.toString(),
                description: selectedItem.description
            });
        }
    }, [itemName, supplements, machines, additionals]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setItemData({ ...itemData, [name]: value });
    };

    const updateItemInCategory = (category, selectedItem) => {
        // Update the item in the respective category
        const updatedCategory = category.map(item => {
            if (item.name === itemName) {
                return {
                    ...item,
                    name: itemData.name,
                    price: Number(itemData.price),
                    quantity: Number(itemData.quantity),
                    description: itemData.description
                };
            }
            return item;
        });

        return updatedCategory;
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // updateItem(itemName, itemData);
        navigate('/Admin');
    };

    return (
        <>
        <div>
            <h2>{itemName} szerkesztése</h2>
        </div>
        </>
    );
};
