import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export function Pay() {
  const navigate = useNavigate();
    const [cardNumber, setCardNumber] = useState('');
    const [expirationDate, setExpirationDate] = useState('');
    const [cvv, setCVV] = useState('');
    const [address, setAddress] = useState('');

    const handleSubmit = (e) => {
      e.preventDefault();
      // Itt végezheted el a fizetést vagy más feldolgozást
  
      // Példa: Ellenőrizd, hogy minden mező ki van-e töltve
      if (cardNumber && expirationDate && cvv && address) {
        // Fizetési logika, adatok feldolgozása stb.
        // Példa: Visszanavigálás a főoldalra a fizetés megerősítése után
        navigate('/thankyou'); // Példa: Átirányítás a köszönőoldalra ('/thankyou')
      } else {
        // Ha valamelyik mező nincs kitöltve, kezeld le ezt a helyzetet
        alert('Kérlek, tölts ki minden mezőt!');
      }
    };

    return (
      <>
        <h2>Fizetés</h2>
        <form onSubmit={handleSubmit}>
        <label>
          Bankkártya száma:
          <input
            type="text"
            value={cardNumber}
            onChange={(e) => setCardNumber(e.target.value)}
            style={{
              backgroundColor: 'lightgrey',
              border: '2px solid red',
              marginBottom: '10px',
              padding: '5px',
              borderRadius: '5px',
            }}
          />
        </label>
        <br></br>
        <label>
          Lejárati dátum:
          <input
            type="text"
            value={expirationDate}
            onChange={(e) => setExpirationDate(e.target.value)}
            style={{
              backgroundColor: 'lightgrey',
              border: '2px solid red',
              marginBottom: '10px',
              padding: '5px',
              borderRadius: '5px',
            }}
          />
        </label>
        <br></br>
        <label>
          CVV:
          <input
            type="text"
            value={cvv}
            onChange={(e) => setCVV(e.target.value)}
            style={{
              backgroundColor: 'lightgrey',
              border: '2px solid red',
              marginBottom: '10px',
              padding: '5px',
              borderRadius: '5px',
            }}
          />
        </label>
        <br></br>
        <label>
          Lakcím:
          <input
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            style={{
              backgroundColor: 'lightgrey',
              border: '2px solid red',
              marginBottom: '10px',
              padding: '5px',
              borderRadius: '5px',
            }}
          ></input>
        </label>
        <br></br>
        <br></br>
        <button type="submit" style={{ color: 'white',backgroundColor: '#cc3333',height: '50px',width: '100px',fontSize: '20px',border: '3px solid white',borderRadius: '10px',marginLeft: '20px' }}>Fizetés</button>
      </form>
      </>

      );
};
