import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export function Pay({ setShowThankYou }) {
  const navigate = useNavigate();
    const [cardNumber, setCardNumber] = useState('');
    const [expirationDate, setExpirationDate] = useState('');
    const [cvv, setCVV] = useState('');
    const [address, setAddress] = useState('');
    const [success, setSuccess] = useState(false);

    const handleSubmit = (e) => {
      e.preventDefault();

      setSuccess(true);
      navigate('/');

      /*
      if (cardNumber && expirationDate && cvv && address) {
        //Adatok ellenőrzése

        //Ha sikeres a vásárlás akkor a főoldalra ugrás
        setSuccess(true);
        navigate('/');
      } else {
        //Nincs minden mező kitöltve
        alert('Kérlek, tölts ki minden mezőt!');
      }
      */
    };

    return (
      <>
        <h2>Fizetés</h2>
        <form onSubmit={handleSubmit}>
        <label>
          Bankkártya száma:
          <input
            type="number"
            value={cardNumber}
            onChange={(e) => setCardNumber(e.target.value)}
            style={{
              backgroundColor: 'lightgrey',
              border: '2px solid red',
              marginBottom: '10px',
              padding: '5px',
              borderRadius: '5px',
            }}
          />
        </label>
        <br></br>
        <label>
          Lejárati dátum:
          <input
            type="number"
            value={expirationDate}
            onChange={(e) => setExpirationDate(e.target.value)}
            style={{
              backgroundColor: 'lightgrey',
              border: '2px solid red',
              marginBottom: '10px',
              padding: '5px',
              borderRadius: '5px',
            }}
          />
        </label>
        <br></br>
        <label>
          CVV:
          <input
            type="number"
            value={cvv}
            onChange={(e) => setCVV(e.target.value)}
            style={{
              backgroundColor: 'lightgrey',
              border: '2px solid red',
              marginBottom: '10px',
              padding: '5px',
              borderRadius: '5px',
            }}
          />
        </label>
        <br></br>
        <label>
          Lakcím:
          <input
            type="text"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            style={{
              backgroundColor: 'lightgrey',
              border: '2px solid red',
              marginBottom: '10px',
              padding: '5px',
              borderRadius: '5px',
            }}
          ></input>
        </label>
        <br></br>
        <br></br>
        <button type="submit" style={{ color: 'white',backgroundColor: '#cc3333',height: '50px',width: '100px',fontSize: '20px',border: '3px solid white',borderRadius: '10px',marginLeft: '20px' }}>Fizetés</button>
      </form>
      </>

      );
};
