import React, { useContext, useState  } from 'react';
import { DataContext } from '../../ContextAPI';
import { useParams } from 'react-router-dom';

export function SearchedItems() {
    const { supplements, machines, additionals, setSupplements, setMachines, setAdditionals } = useContext(DataContext);
    const { searchString } = useParams();
    const [error, setError] = useState(null);

    const filterProducts = (products) => {
        return products.filter((product) =>
            product.name.toLowerCase().includes(searchString.toLowerCase())
        );
    };

    const filteredSupplements = filterProducts(supplements);
    const filteredMachines = filterProducts(machines);
    const filteredAdditionals = filterProducts(additionals);

    const getImagePath = (imageName) => {
        let path = null;
        try {
            path = require(`../../resource/supplements/${imageName}`);
        } catch (error) {
            try {
                path = require(`../../resource/machines/${imageName}`);
            } catch (error) {
                try {
                    path = require(`../../resource/additional/${imageName}`);
                } catch (error) {
                    console.error('Image not found');
                }
            }
        }
        return path;
    };

    const updateProducts = (category, updatedProducts) => {
        if (category === 'supplement') {
            setSupplements(updatedProducts);
        } else if (category === 'machine') {
            setMachines(updatedProducts);
        } else if (category === 'additional') {
            setAdditionals(updatedProducts);
        }
    };

    const addToCart = (itemName) => {
        const selectedItem = [...filteredSupplements, ...filteredMachines, ...filteredAdditionals].find(
            (item) => item.name === itemName
        );

        const updatedProducts = [...supplements,...machines,...additionals].map((item) => {
            if(item.name === itemName){
                if(item.quantity > 0){
                    console.log(item.quantity);
                    return { ...item, quantity: item.quantity - 1 };
                }
                else{
                    setError(`A "${itemName}" elfogyott`);
                    return item;
                }
            }
            return item;
        });

        if (selectedItem) {
            updateProducts(selectedItem.category, updatedProducts.filter(item => item.category === selectedItem.category));
        }
        //Kosárhoz adás
    };

    const handleCloseError = () => {
        setError(null);
    };

    return (
        <div>
            <h2>Keresés eredménye</h2>
            <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
                {[...filteredSupplements, ...filteredMachines, ...filteredAdditionals].map((item,index) => (
                    <div key={index} style={{ display: 'flex', alignItems: 'flex-start', marginBottom: '20px', marginRight: '20px', width: '800px' }}>
                        {item.img && (
                            <img src={getImagePath(item.img)} alt={item.name} style={{ width: '350', marginRight: '20px' }}/>
                        )}
                        <div>
                        <h3 style={{ fontSize: '30px' }}>{item.name}</h3>
                        <p style={{ color: 'red' }}>Ár: {item.price} Ft</p>
                        <p>{item.description}</p>
                        <div style={{ float: 'left',objectPosition: 'bottom' }}>
                          <button onClick={() => addToCart(item.name)} style={{ color: 'white',backgroundColor: '#cc3333',height: '50px',width: '100px',fontSize: '20px',border: '3px solid white',borderRadius: '10px' }}>Kosárba</button>
                        </div>
                      </div>
                    </div>
                ))}
            </div>
            {error && (
                <div style={{ position: 'fixed', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', padding: '20px', background: 'grey', zIndex: '999', boxShadow: '0 0 10px rgba(0, 0, 0, 0.3)' }}>
                    <h3>Error</h3>
                    <p>{error}</p>
                    <button onClick={handleCloseError}>Bezárás</button>
                </div>
            )}
        </div>
    );
};
