import React, { useState } from 'react';
import { useData } from '../../ContextAPI';

export function AddItem() {
    const { supplements, setSupplements, machines, setMachines, additionals, setAdditionals } = useData();

    const [itemName, setItemName] = useState('');
    const [itemPrice, setItemPrice] = useState('');
    const [itemQuantity, setItemQuantity] = useState('');
    const [itemDescription, setItemDescription] = useState('');
    const [itemType, setItemType] = useState('Supplement');

    const handleAddItem = () => {
        const newItem = {
            name: itemName,
            price: itemPrice,
            quantity: itemQuantity,
            description: itemDescription,
        };

        switch (itemType) {
            case 'Supplement':
                setSupplements([...supplements, newItem]);
                break;
            case 'Machine':
                setMachines([...machines, newItem]);
                break;
            case 'Additional':
                setAdditionals([...additionals, newItem]);
                break;
            default:
                break;
        }

        setItemName('');
        setItemPrice('');
        setItemQuantity('');
        setItemDescription('');
        setItemType('Supplement');
    };

    return (
        <>
        <div>
            <h2>Új termék hozzáadása</h2>
            <form onSubmit={(e) => {
                    e.preventDefault();
                    handleAddItem();
                }}>
                    <label>
                        Termék neve:
                        <input type="text" value={itemName} onChange={(e) => setItemName(e.target.value)} />
                    </label>
                    <br />
                    <label>
                        Termék neve:
                        <input type="text" value={itemName} onChange={(e) => setItemName(e.target.value)} />
                    </label>
                    <br />
                    {/* További input mezők a termék többi tulajdonságára */}
                    <label>
                        Termék típusa:
                        <select value={itemType} onChange={(e) => setItemType(e.target.value)}>
                            <option value="Supplement">Supplement</option>
                            <option value="Machine">Machine</option>
                            <option value="Additional">Additional</option>
                        </select>
                    </label>
                    <button type="submit">Hozzáadás</button>
                </form>
        </div>
        </>
    );
};
