import React, { useState, useEffect } from 'react';
import supplementsData from '../../datas/supplements.json';

export function Supplements() {
  const [supplements, setSupplements] = useState([]);

  useEffect(() => {
    setSupplements(supplementsData.supplements);
  }, []);

  const getImagePath = (imageName) => {
    return require(`../../resource/supplements/${imageName}`);
  };

  const addToCart = (itemName) => {
    // Itt lehetne hozzáadni a terméket a kosárhoz
    console.log(`"${itemName}" hozzáadva a kosárhoz`);
  };

  return (
    <>
      <h2 style={{ fontSize: '50px', textAlign: 'center' }}>Táplálékkiegészítők</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
        {supplements.map((item, index) => (
          <div key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '20px', marginRight: '20px', width: '800px' }}>
            <div style={{ position: 'relative', width: '350px' }}>
              <img src={getImagePath(item.img)} alt={item.name} style={{ width: '100%', display: 'block' }} />
              <div style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)' }}>
                <button onClick={() => addToCart(item.name)}>Kosárba</button>
              </div>
            </div>
            <div style={{ marginLeft: '20px' }}>
              <h3 style={{ fontSize: '30px' }}>{item.name}</h3>
              <p style={{ color: 'red' }}>Ár: {item.price} Ft</p>
              <p>{item.description}</p>
            </div>
          </div>
        ))}
      </div>
    </>
  );
};
