import React, { useContext } from 'react';
import { DataContext } from '../../ContextAPI';
import { useParams } from 'react-router-dom';

export function SearchedItems() {
    const { supplements, machines, additionals } = useContext(DataContext);
    const { searchString } = useParams();

    const filterProducts = (products) => {
        return products.filter((product) =>
            product.name.toLowerCase().includes(searchString.toLowerCase())
        );
    };

    const filteredSupplements = filterProducts(supplements);
    const filteredMachines = filterProducts(machines);
    const filteredAdditionals = filterProducts(additionals);

    return (
        <div>
            <h2>Keresés eredménye</h2>
            <ul>
                {[...filteredSupplements, ...filteredMachines, ...filteredAdditionals].map((product,index) => (
                    <li key={index}>
                        <h3>{product.name}</h3>
                        <p>Ár: {product.price}</p>
                        <p>{product.description}</p>
                        {product.img && <img src={require(`../../resource/additional/${product.img}`)} alt={product.name} />}
                        {product.img && <img src={require(`../../resource/supplements/${product.img}`)} alt={product.name} />}
                        {product.img && <img src={require(`../../resource/machines/${product.img}`)} alt={product.name} />}
                        {/* További termék részletek */}
                    </li>
                ))}
            </ul>
        </div>
    );
};
