import React, { useState } from 'react';

export function Register() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Submitted username:', username);
    console.log('Submitted password:', password);
    console.log('Submitted email:', password);
  };

  return (
    <>
    <h2>Regisztráció</h2>
    <form onSubmit={handleSubmit}>
      <label>
        Felhasználónév:
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{
            backgroundColor: 'lightgrey',
            border: '2px solid red',
            marginBottom: '10px',
            padding: '5px',
            borderRadius: '5px',
          }}
        />
      </label>
      <br />
      <label>
        Jelszó:
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{
            backgroundColor: 'lightgrey',
            border: '2px solid red',
            marginBottom: '10px',
            padding: '5px',
            borderRadius: '5px',
          }}
        />
      </label>
      <br />
      <label>
        E-Mail:
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={{
            backgroundColor: 'lightgrey',
            border: '2px solid red',
            marginBottom: '10px',
            padding: '5px',
            borderRadius: '5px',
          }}
        />
      </label>
      <br />
      <button type="submit" style={{ color: 'white',backgroundColor: '#cc3333',height: '50px',width: '200px',fontSize: '20px',border: '3px solid white',borderRadius: '10px',marginLeft: '20px' }}>Regisztráció</button>
    </form>
  </>
    );
};
